import React from 'react';
import OneHark from "./OneHark";
import OneNavik from "./OneNavik";
import SimpleTextarea from "../UI/SimpleTextarea";
import "./Stats.css"
import Accordion from "../UI/Accordion";
import StatsWithSimpleInput from "./StatsWithSimpleInput";
import IncDecInput from "../UI/IncDecInput";

const harksName = ["Сила", "Телосложение", "Ловкость", "Интеллект", "Мудрость", "Харизма"];
const naviksName = [
    "Атлетика",
    "Выносливость",
    "Акробатика",
    "Ловкость рук",
    "Незаметность",
    "Анализ",
    "Естественные науки",
    "Социальные науки",
    "Бдительность",
    "Внимательность",
    "Выживание",
    "Медицина",
    "Уход за животными",
    "Чувство магии",
    "Вера",
    "Воля",
    "Выступление",
    "Запугивание",
    "Обман",
    "Убеждение"
]
const vladeniaName = [
    [
    "Использование парного рукопашного оружия (Ученик/Ветеран/Мастер) (Сила)",
    "Использование двуручного рукопашного оружия (Ученик/Ветеран/Мастер) (Сила)",
    "Использование простого рукопашного оружия (Ученик/Ветеран/Мастер) (Сила)",
    "Использование фехтовального оружия (Ученик/Ветеран/Мастер) (Ловкость)",
    "Использование луков и арбалетов (Ученик/Ветеран/Мастер) (Ловкость)",
    "Использование универсального, притягивающего и нестандартного дальнобойного оружия (Ученик/Ветеран/Мастер) (Ловкость)"
    ],
    [
    "Использование лёгких доспехов (Ловкость)",
    "Испольование средних доспехов (Телосложение)",
    "Использование тяжёлых доспехов (Сила)",
    "Использование щитов (Сила)"
    ],
    [
    "Магия Водоворота душ (Ученик/Адепт/Мастер) (Мудрость)",
    "Магия Потоков первородной магии (Ученик/Адепт/Мастер) (Мудрость)",
    "Магия Вечных пустошей (Ученик/Адепт/Мастер) (Мудрость)",
    "Магия Великого урагана (Ученик/Адепт/Мастер) (Мудрость)",
    "Магия Солнечного горнила (Ученик/Адепт/Мастер) (Мудрость)",
    "Магия Сияния (Ученик/Адепт/Мастер) (Мудрость)",
    "Магия Грани реальности (Ученик/Адепт/Мастер) (Мудрость)"
    ],
    [
    "Взлом/владение отмычками (Ловкость рук(Ловкость))",
    "Владение ловушками (Ловкость рук(Ловкость))",
    "Владение играми (Анализ/Ловкость рук(Ловкость))",
    "Карманные кражи (Ловкость рук(Ловкость))",
    "Владение инструментами плотника (Ловкость)",
    "Владение инструментами ювелира (Ловкость)",
    "Владение инструментами гончара (Ловкость)",
    "Владение инструментами ткача (Ловкость)",
    "Владение инструментами пивовара (Мудрость)",
    "Владение инструментами кузнеца (Атлетика(Сила))",
    "Владение музыкальными инструментами (Харизма/Интелект)",
    "Владение набором фокусника (Выступление/Лоавкость рук(Ловкость))",
    "Владение набором артиста(включая грим) (Харизма)"
    ],
    [
    "Знание улиц (Эрудиция: Социальные науки(Интелект))",
    "Знание высшего света (Эрудиция: Социальные науки(Интелект))",
    "Знание искусства (Эрудиция: Социальные науки(Интелект))",
    "Знание географии (Эрудиция: Социальные науки(Интелект))",
    "Знание истории (Эрудиция: Социальные науки(Интелект))",
    "Знание теологии (Эрудиция: Социальные науки(Интелект))",
    "Знание технологий (Эрудиция: Естественные науки(Интелект))",
    "Знаний магических наук (Эрудиция: Естественные науки(Интелект))",
    "Знание точных наук (Эрудиция: Естественные науки(Интелект))",
    "Знание природы (Эрудиция: Естественные науки(Интелект))",
    "Знание планов (Эрудиция: Естественные науки(Интелект))"
    ],
    [
    "Знание общего языка (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание человеческого языка (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание языка Диких (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание говора Морозных земель (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание рыков Гноллов (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание языка Ракшасс (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание языка Арахнид (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание языка Кобольдов (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание языка Капп (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание языка Сарангаев (Харизма/Эрудиция: Социальные науки(Интелект))",
    "Знание змеинного наречия (Харизма/Эрудиция: Социальные науки(Интелект))"
    ],
    [
    "Кулачный бой (Сила)",
    "Борьба (Атлетика(Сила))",
    "Травничество (Медицина(Мудрость))",
    "Мореходство (Мудрость/Интелект)"
    ]
]
const vladeniaBlockInformation = [
    {
        name: "Оружейные",
        topInformation: <div>
            <div>0 - не имеется, 1 - ученик, 2 - ветеран, 3 - мастер</div>
            <div>Максимальный ранг - ученик при СИЛ/ЛОВ хотя бы +1, Адепт при СИЛ/ЛОВ хотя бы +6 и 45 мощи, Мастер при СИЛ/ЛОВ хотя бы +8 и 60 мощи.
                Текущий максимальный ранг: ???</div>
            <div>На максимальное кол-во ограничения не накладываются</div>
        </div>
    },
    {
        name: "Защитные",
        topInformation: <div>
            <div>0 - не имеется, 1 - имеется</div>
            <div>При отсутвии владения вы получаете помеху на любые действия в этом типе доспехов</div>
        </div>
    },
    {
        name: "Мистические",
        topInformation: <div>
            <div>0 - не имеется, 1 - ученик, 2 - ветеран, 3 - мастер</div>
            <div>Максимальный ранг - ученик при МДР хотя бы +1, Адепт при МДР хотя бы +6 и 45 мощи, Мастер при МДР хотя бы +8 и 60 мощи.
                Текущий максимальный ранг: ???</div>
            <div>Максимальное кол-во школ = уровню навыка Чувство магии.
                Текущее: ???</div>
        </div>
    },
    {
        name: "Плутовские и владения инструментами",
        topInformation: <div>
            <div>0 - не имеется, 1 - имеется</div>
        </div>
    },
    {
        name: "Знания",
        topInformation: <div>
            <div>0 - не имеется, 1 - имеется</div>
        </div>
    },
    {
        name: "Языки",
        topInformation: <div>
            <div>0 - не имеется, 1 - имеется</div>
        </div>
    },
    {
        name: "Прочее",
        topInformation: <div>
            <div>0 - не имеется, 1 - имеется</div>
        </div>
    },
]

const Stats = ({charList}) => {
    let harksAndNaviksElement = [];
    let vladeniaElement = [];

    function getNaviksEl(begin, len) {
        let naviksContainer = [];
        for (let i=begin; i < begin + len; i++) {
            naviksContainer.push(
                <OneNavik key={"navik" + i} name={naviksName[i]} val={charList.naviks.value[i]} setVal={charList.naviks.setValue(i)}></OneNavik>
            )
        }
        return naviksContainer;
    }

    for (let i=0; i < harksName.length; i++) {
        harksAndNaviksElement.push(
            <div className={"harks-and-naviks-block"}>
                <OneHark key={"harks" + i} name={harksName[i]} val={charList.harks.value[i]} setVal={charList.harks.setValue(i)}></OneHark>
                {i < 2 ? getNaviksEl(i, 1) :
                    i < 4 ? getNaviksEl(i + (i - 2) * 2, 3) :
                    getNaviksEl(i + 4 + (i - 4) * 5, 6) }
            </div>
        )
    }

    function getVladeniaShift(index) {
        let shift = 0;
        for (let i=0; i < index; i++) {
            shift += vladeniaName[i].length;
        }
        return shift;
    }

    function getVladeniaElement(index) {
        let vladeniaInBlockContainer = [];
        for (let j=0; j < vladeniaName[index].length; j++) {
            vladeniaInBlockContainer.push(
                <OneNavik key={"vladenia" + index + "-" + j} name={vladeniaName[index][j]} val={charList.vladenia.value[getVladeniaShift(index) + j]} setVal={charList.vladenia.setValue(getVladeniaShift(index) + j)}></OneNavik>
            )
        }
        return vladeniaInBlockContainer;
    }

    for (let i=0; i < vladeniaName.length; i++) {
        vladeniaElement.push(<Accordion title={vladeniaBlockInformation[i].name} content={
            <div>
                <div className={"bold-red-text vladenia-block-information"} >{vladeniaBlockInformation[i].topInformation}</div>
                <div>{getVladeniaElement(i)}</div>
            </div>
        }/>)
    }

    function boneInputCreate (val, setVal) {
        return <IncDecInput className={"add-val-stroke"} val={val < 4 ? "1к4-" + (4 - val) / 2 : val < 12 ? "1к" + val : "1к12+" + (val - 12) / 2}
                            setVal={setVal} decFunc={() => setVal(+val - 2)} incFunc={() => setVal(+val + 2)}/>

    }

    return (
        <div>
            <div className={"harks-and-naviks-blocks"}>
                {harksAndNaviksElement}
            </div>
            <div className={"vladenia"}>
                <p className={"big-bold-red-text"}>Владения:</p>
                {vladeniaElement}
            </div>
            <div className={"other-stats"}>
                {boneInputCreate(charList.boneHits.value[0], charList.boneHits.setValue(0))}
                <IncDecInput className={"add-val-stroke"} val={"1к" + charList.boneHits.value[0]} setVal={charList.boneHits.setValue(0)}
                             decFunc={() => charList.boneHits.setValue(0)(+charList.boneHits.value[0] - 2)} incFunc={() => charList.boneHits.setValue(0)(+charList.boneHits.value[0] + 2)}/>
                <StatsWithSimpleInput name={"Кость хитов"} val={charList.boneHits.value[0]} setVal={charList.boneHits.setValue(0)}/>
                <StatsWithSimpleInput name={"Кость маны"} val={charList.boneMane.value[0]} setVal={charList.boneMane.setValue(0)}/>
                <StatsWithSimpleInput name={"Увеличения максимума хитов"} val={charList.addMaxHits.value[0]} setVal={charList.addMaxHits.setValue(0)}/>
                <StatsWithSimpleInput name={"Увеличения максимума маны"} val={charList.addMaxMane.value[0]} setVal={charList.addMaxMane.setValue(0)}/>
            </div>
        </div>
    );
};

export default Stats;
