import React from 'react';
import OneHark from "./OneHark";
import SimpleTextarea from "../UI/SimpleTextarea";

let harksName = ["Сила", "Телосложение", "Ловкость", "Интеллект", "Мудрость", "Харизма"];

const Stats = ({charList}) => {
    let harksElement = [];

    for (let i=0; i < harksName.length; i++) {
        harksElement.push(
        <OneHark key={"harks" + i} name={harksName[i]} val={charList.harks.value[i]} setVal={charList.harks.setValue(i)}></OneHark>
        )
    }

    return (
        <div>
            {harksElement}
            <SimpleTextarea
            style={{width: "100%", height: "400px", textAlign: "start"}}
            value = {charList.harks.value[0]}
            onChange={e => {charList.harks.setValue(0)}}
            placeholder={"Серый и убогий"}/>
        </div>
    );
};

export default Stats;
