import React from 'react';
import SimpleInput from "./SimpleInput";
import "./IncDecInput.css"

const IncDecInput = ({val, setVal}) => {
    return (
        <div>
            <button onClick={() => {setVal=+val-1}} className={"left-button"} type={"button"}>"-"</button>
            <SimpleInput val={val} setVal={setVal}></SimpleInput>
            <button onClick={() => {setVal=+val+1}} className={"right-button"} type={"button"}>"+"</button>
        </div>

    );
};

export default IncDecInput;
