import React from 'react';
import IncDecInput from "../UI/IncDecInput";
import "./OneNavik.css";

const getLvl = new Map([
    [0, 'Отсутствие(+0)'],
    [1, 'Владение(+1к4)'],
    [2, 'Компетентность(+1к8)'],
    [3, 'Мастерство(+1к12)']
])

const OneNavik = ({name, val, setVal}) => {
    return (
        <div className={"one-navik stats-with-field"}>
            <label className={"bold-red-text"}>{name + ":"}</label>
            <IncDecInput className={"one-navik-inp"} val={(+val >= 0 && +val <= 3) ? getLvl.get(+val) : +val} setVal={setVal}
            decFunc={() => setVal(+val-1)} incFunc={() => setVal(+val+1)}/>
        </div>
    );
};

export default OneNavik;
