import React from 'react';
import IncDecInput from "../UI/IncDecInput";
import "./Aside.css"
import SimpleInput from "../UI/SimpleInput";
import DropValueInput from "../UI/DropValueInput";
import StandartDropValueInput from "../UI/StandartDropValueInput";

const Aside = ({charList, evalList}) => {
    return (
        <aside>
            <div className={"aside-information-block"}>
                <header className={"char-name"}>
                    <p>Имя:</p>
                    <SimpleInput className={"simple-input"} placeholder={"Потерявший имя"} value={charList.charName.value[0]} onChange={(e) => {charList.charName.setValue(0)(e.target.value)}}/>
                </header>
                <main>
                    <div className={"stats-block"}>
                        <p>Мощь:</p>
                        <IncDecInput val={charList.power.value[0]} setVal={charList.power.setValue(0)}/>
                    </div>
                    <div className={"stats-block"}>
                        <p>Хиты:</p>
                        <IncDecInput val={charList.curHits.value[0]} setVal={charList.curHits.setValue(0)}/>
                    </div>
                    <div className={"stats-block"}>
                        <p>Мана:</p>
                        <IncDecInput val={charList.curMane.value[0]} setVal={charList.curMane.setValue(0)}/>
                    </div>
                    <div className={"stats-block"}>
                        <p>Вдохновение:</p>
                        <IncDecInput val={charList.inspiration.value[0]} setVal={charList.inspiration.setValue(0)}/>
                    </div>
                    <div>
                        <p>Максимум хитов:</p>
                        {/*<StandartDropValueInput val={charList.curHits.value[0]} setVal={charList.curHits.setValue(0)}/>*/}
                        {/*<DropValueInput val={charList.curHits.value[0]} setVal={charList.curHits.setValue(0)}*/}
                        {/*                funcArray={[{name: "-1", func: () => {charList.curHits.setValue(0)(+charList.curHits.value[0] - 1)}},*/}
                        {/*                    {name: "+1", func: () => {charList.curHits.setValue(0)(+charList.curHits.value[0] + 1)}}]}/>*/}
                        <p>{evalList.maxHits}</p>
                        <p>Максимум маны:</p>
                        <p>{evalList.maxMane}</p>
                    </div>

                </main>
            </div>
        </aside>
    );
};

export default Aside;
