import CHANGE_FIELD from "./actionCreators/CHANGE_FIELD";

const initialState = {
    charName: "",
    harks: Array(6).fill(0)
};


export const charlistReducer = (state = initialState, action) => {
    switch (action.type) {
        case CHANGE_FIELD:
            let newHarks = Array(6).fill(0);
            for (let i = 0; i < newHarks.length; i++) {
                newHarks[i] = state.harks[i];
            }
            newHarks[action.payload.field] = action.payload.value;
            return {...state, harks: newHarks};
        case "CHANGE_NAME":
            return {...state, charName: action.payload.name}
        default:
            return state;
    }
}
