import React from 'react';
import IncDecInput from "../UI/IncDecInput";

const Aside = ({charList}) => {
    return (
        <aside>
            <div className={"aside-information-block"}>
                <header className={"char-name"}>
                    <input className={"simple-input"} placeholder={"Потерявший имя"} value={charList.charName.value[0]} onChange={(e) => {charList.charName.setValue(0)(e.target.value)}}/>
                </header>
                <div>
                    <p>
                        Мощь:
                    </p>
                    <IncDecInput className={"powerBlock"} val={charList.power.value[0]} setVal={charList.power.setValue(0)}/>
                </div>
            </div>
        </aside>
    );
};

export default Aside;
