import React from 'react';
import SimpleInput from "./SimpleInput";
import "./IncDecInput.css";
import "./SimpleInput.css";
import SimpleButton from "./SimpleButton";

const IncDecInput = ({val, setVal, ...props}) => {
    return (
        <div className={"inc-dec-input"}>
            <SimpleButton onClick={() => {setVal(+val-1)}} className={"left-button"} type={"button"}>-</SimpleButton>
                <SimpleInput val={val} setVal={setVal}></SimpleInput>
            <SimpleButton onClick={() => {setVal(+val+1)}} className={"right-button"} type={"button"}>+</SimpleButton>
        </div>

    );
};

export default IncDecInput;
