import CHANGE_FIELD from "./actionCreators/CHANGE_FIELD";

const initialState = {
    charName: ""
}

export const charlistReducer = (state = initialState, action) => {
    switch (action.type) {
        case CHANGE_FIELD:
            let newState = state;
            newState[action.payload] = action.value;
            return newState;
        default:
            return state;
    }
}
